def update_tool_icons():
    db = SessionLocal()
    try:
        tools = db.query(Tool).all()
        updated_count = 0
        for tool in tools:
            if tool.icon and tool.icon in SVG_MAP:
                tool.icon = SVG_MAP[tool.icon]
                updated_count += 1
            elif tool.icon and not tool.icon.startswith('<svg'):
                # Fallback for unknown icons - use 'code' or just leave it?
                # Let's leave it if we don't have a mapping, or maybe map to 'code' if it looks like a feather icon name
                pass
        
        db.commit()
        print(f"Successfully updated {updated_count} tool icons to SVG.")
    except Exception as e:
        print(f"Error updating tool icons: {e}")
        db.rollback()
    finally:
        db.close()

if __name__ == "__main__":
    update_tool_icons()
